import React, { Component } from "react";
import Dropdown from "../dropdown/dropdown";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import { Container, Button } from "react-bootstrap";

class Dashboard extends Component {
  onLogoutClick = (e) => {
    e.preventDefault();
    this.props.logoutUser();
  };

  render() {
    const { user } = this.props.auth;
    return (
      <Container>
        <Dropdown date="weekday" day="Monday" />
        <Dropdown date="weekday" day="Tuesday" />
        <Dropdown date="weekday" day="Wednesday" />
        <Dropdown date="weekday" day="Thursday" />
        <Dropdown date="weekday" day="Friday" />
        <Dropdown date="weekend" day="Saturday" />
        <Dropdown date="weekend" day="Sunday" />
        <h4>
          <b>Hey there,</b> {user.name.split(" ")[0]}
        </h4>
        <Button variant="dark" onClick={this.onLogoutClick}>
          Logout
        </Button>
      </Container>
    );
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logoutUser })(Dashboard);
